function stripHtmlTags(input) {
    return input.replace(/<[^>]+>/g, '').replace(/&nbsp;/g, ' ').replace(/\n/g, ' ');
}

function parseAttachments(items) {
  const results = [];

  for (const item of items) {
    if (typeof item?.binary === 'object') {
      for (const key of Object.keys(item.binary)) {
          results.push({
              json: {
                  fileName: item.binary[key].fileName
              },
              binary: {
                  data: item.binary[key],
              }
          });
      }
    }
  }

  return results;
}

const input = JSON.parse($input.first().json.output);

if (!input || !input.bookingRelated) {
   return {
     bookingRelated: false
  };
}

const from = typeof $('Microsoft Outlook Trigger').first().json.from === 'object' ? $('Microsoft Outlook Trigger').first().json.from.emailAddress.name + '<' + $('Microsoft Outlook Trigger').first().json.from.emailAddress.address + '>' :
    typeof $('Microsoft Outlook Trigger').first().json.internetMessageId === 'string' ? $('Microsoft Outlook Trigger').first().json.internetMessageId : 'UNKNOWN SENDER';

const hasAttachments = $('Microsoft Outlook Trigger').first().json.hasAttachments;

const attachments = hasAttachments ? parseAttachments( $('Microsoft Outlook Trigger').all()) : [];

const bookingKnowledge = {
  bookingRelated: true,
  emailCategoryCode: input.emailCategoryCode,
  emailRecipientType: input.emailRecipientType,
  inferredLanguage: input.inferredLanguage,
  emailId: $('Microsoft Outlook Trigger').first().json.id,
  conversationId: $('Microsoft Outlook Trigger').first().json.conversationId,
  fileReference: input.fileReference,
  receivedAt: $('Microsoft Outlook Trigger').first().json.receivedDateTime,
  from,
  subject: $('Microsoft Outlook Trigger').first().json.subject,
  body: stripHtmlTags($('Microsoft Outlook Trigger').first().json.body.content),
  hasAttachments,
  attachments,
};

return bookingKnowledge;

