try {
  const input = JSON.parse($input.first().json.output);

  let firstValidAttachment = null;

  for (const attachment of $("Build booking knowledge").first().json.attachments) {
    if ( attachment.binary.data.mimeType === 'application/pdf' && firstValidAttachment ==  null) { 
      firstValidAttachment = {
        data: attachment.binary.data.data,
        mimeType: attachment.binary.data.mimeType,
        fileName: attachment.json.fileName,
      };
   }
 }

  return {
    voucherIdentifier: input.voucherIdentifier,
    date: input.date,
    transactionIdentifier: input.transactionIdentifier,
    fileIdentifier: input.fileIdentifier,
    supplier: input.supplier,
    status: input.status,
    confirmationNumber: input.confirmationNumber,
    firstValidAttachment,
  };
}
catch {
  return {};
}

